import React, { Component, useState } from 'react';
import './App.css';
import UserInput from './UserInput/UserInput';
import UserOutput from './UserOutput/UserOutput';

const App = props => {
  const [usersState, setUsers] = useState({
    users: [
      { username: 'Ricky'},
      { username: 'Johan'}
    ]
  });

  const clickButton = (newName) => {
    setUsers({
      users: [
        { username: newName},
        { username: 'Johan Santanna'}
      ]
    });
  }

  const nameChangedHandler = (event) => {
    setUsers({
      users: [
        { username: newName},
        { username: 'Johan Santanna'}
      ]
    });
  }

  return (
    <div className="App">
      <h1>Test</h1>
      <UserInput />
      <button onClick={clickButton.bind(this, 'Tom Holland!!')}>Click Me</button>
      <UserOutput username={usersState.users[0].username} />
      <UserOutput username={usersState.users[1].username}
      />
    </div>
  );
}

export default App;




