import React from 'react';
import './App.css';
import UserInput from './UserInput/UserInput';
import UserOutput from './UserOutput/UserOutput';

function App() {
  state = {
    users:[
      {username: 'Ricky'},
      {username: 'Joahn'}
    ]
  }


  return (
    <div className="App">
      <h1>Test</h1>
      <UserInput />
      <UserOutput username="Ricky"/>
      <UserOutput username="Joahon"/>
    </div>
  );
}

export default App;

